
public class TestPyramid {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//program for printing pyramid pattern in java
		for(int i=0;i<=5;i++) {
			//logic for pyramid
			for(int j=1;j<=i;j++) {
				System.out.print("*");
				System.out.print("\t");
			}
			System.out.println();
		}	

	}

}
